# http://slides.sensuapp.org/image/img/sensu_fan.png

require 'json'
require 'net/http'

class Sensu
  SENSU_URL = 'https://sensu.apps.mine.nu/results'

  ANY_BAR_STATUS_OK = ''
  ANY_BAR_STATUS_CRITICAL = ''
  ANY_BAR_STATUS_WARNING = ''
  ANY_BAR_STATUS_UNKNOWN = ''

  JSON_UNKNOWN = [ { 'check' => { 'status' => 3 } } ]

  def update!
    puts status
  end

  private

    def url
      @url ||= URI.parse(SENSU_URL)
    end

    def json
      @json ||= JSON.parse(Net::HTTP.get(url))
    rescue
      JSON_UNKNOWN
    end

    def all_status
      json.map { |x| x['check']['status'] }.uniq
    end

    def status_colour
      status_colour = if all_status.include?(2)
                        [ ANY_BAR_STATUS_CRITICAL, 'CRITICAL' ]
                      elsif all_status.include?(1)
                        [ ANY_BAR_STATUS_WARNING, 'WARNING' ]
                      elsif all_status.include?(0)
                        [ ANY_BAR_STATUS_OK, 'OK' ]
                      else
                        [ ANY_BAR_STATUS_UNKNOWN, 'UNKNOWN' ]
                      end
    end

    def status
      status = []
      status << '| image=%s' % [ status_colour[0] ]
      status << '---'
      status << 'Status: %s | href=https://monitor.apps.mine.nu/#/events' % [ status_colour[1] ]
      status.join("\n")
    end

end

Sensu.new.update!
